import React, { useState, useEffect } from 'react';

function DeliveryOptimizerSinglePage() {
  // State for daily customer count.
  const [dailyCustomerCount, setDailyCustomerCount] = useState(0);

  // Customer-related state.
  const [customers, setCustomers] = useState([]);
  const [selectedCustomerIds, setSelectedCustomerIds] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  // Truck and product details.
  const [numTrucks, setNumTrucks] = useState(0);
  const [productWeight, setProductWeight] = useState(0);

  // Optimization result.
  const [optimizationResult, setOptimizationResult] = useState(null);

  // Sample customers data; in a real-world scenario, load this via an API call.
  useEffect(() => {
    const sampleCustomers = [
      { id: 1, name: 'Alice Johnson', address: '123 Main St, Cityville', location: [4, 4] },
      { id: 2, name: 'Bob Smith', address: '456 Oak Ave, Townsville', location: [2, 0] },
      { id: 3, name: 'Charlie Brown', address: '789 Pine Rd, Villageton', location: [8, 0] },
      { id: 4, name: 'Diana Prince', address: '101 Maple St, Metropolis', location: [0, 1] },
      { id: 5, name: 'Ethan Hunt', address: '202 Birch Blvd, Springfield', location: [1, 8] },
      { id: 6, name: 'Fiona Gallagher', address: '303 Cedar Dr, Riverdale', location: [5, 8] },
      { id: 7, name: 'George Bailey', address: '404 Elm St, Bedford Falls', location: [7, 8] },
      // ... add more sample customers as needed.
    ];
    setCustomers(sampleCustomers);
  }, []);

  // Toggle customer selection.
  const handleCustomerToggle = (customerId) => {
    setSelectedCustomerIds((prevSelected) =>
      prevSelected.includes(customerId)
        ? prevSelected.filter((id) => id !== customerId)
        : [...prevSelected, customerId]
    );
  };

  // Filter customers based on the search term.
  const filteredCustomers = customers.filter(
    (customer) =>
      customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      customer.address.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Handler to submit the form.
  const handleSubmit = async () => {
    const selectedCustomers = customers.filter((c) =>
      selectedCustomerIds.includes(c.id)
    );
    const locations = selectedCustomers.map((c) => c.location);

    const payload = {
      // Match these keys with what your backend expects.
      locations: locations,
      num_vehicles: numTrucks,
      depot: 0,                     // Adjust if you have a designated depot location.
      daily_customers: dailyCustomerCount,
      total_weight: productWeight,
    };

    try {
      const response = await fetch("http://localhost:5000/optimize", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const jsonData = await response.json();
      setOptimizationResult(jsonData);
    } catch (error) {
      console.error("Error optimizing routes:", error);
    }
  };

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '2rem' }}>
      <h1>Delivery Optimizer</h1>

      {/* Daily Customer Count */}
      <div style={{ marginBottom: '1rem' }}>
        <label>
          <strong>Daily Customer Count:</strong>
          <input
            type="number"
            min="0"
            value={dailyCustomerCount}
            onChange={(e) => setDailyCustomerCount(Number(e.target.value))}
            style={{ marginLeft: '10px', padding: '5px' }}
          />
        </label>
      </div>

      {/* Customer Search & Selection */}
      <div style={{ marginBottom: '1rem' }}>
        <h2>Select Customers for Delivery</h2>
        <input
          type="text"
          placeholder="Search customers..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: '10px', padding: '5px', width: '100%' }}
        />
        <div
          style={{
            height: '250px',
            overflowY: 'auto',
            border: '1px solid #ccc',
            padding: '10px',
          }}
        >
          {filteredCustomers.map((customer) => (
            <div
              key={customer.id}
              onClick={() => handleCustomerToggle(customer.id)}
              style={{
                padding: '8px',
                marginBottom: '4px',
                cursor: 'pointer',
                borderRadius: '4px',
                backgroundColor: selectedCustomerIds.includes(customer.id)
                  ? '#e0e0e0'
                  : 'transparent',
              }}
            >
              <strong>{customer.name}</strong>
              <br />
              <small>{customer.address}</small>
            </div>
          ))}
          {filteredCustomers.length === 0 && (
            <p>No customers match your search criteria.</p>
          )}
        </div>
      </div>

      {/* Truck & Product Details */}
      <div style={{ marginBottom: '1rem' }}>
        <h2>Truck & Product Details</h2>
        <div style={{ marginBottom: '0.5rem' }}>
          <label>
            Number of Trucks Needed:
            <input
              type="number"
              min="0"
              value={numTrucks}
              onChange={(e) => setNumTrucks(Number(e.target.value))}
              style={{ marginLeft: '10px', padding: '5px' }}
            />
          </label>
        </div>
        <div>
          <label>
            Product Weight (kg):
            <input
              type="number"
              min="0"
              value={productWeight}
              onChange={(e) => setProductWeight(Number(e.target.value))}
              style={{ marginLeft: '10px', padding: '5px' }}
            />
          </label>
        </div>
      </div>

      {/* Submit Button */}
      <button onClick={handleSubmit} style={{ padding: '10px 20px', fontSize: '16px' }}>
        Submit for Optimized Route
      </button>

      {/* Display the Optimization Result */}
      {optimizationResult && (
        <div style={{ marginTop: '2rem' }}>
          <h2>Optimization Result</h2>
          <pre style={{ whiteSpace: 'pre-wrap', textAlign: 'left', background: '#f7f7f7', padding: '10px', borderRadius: '4px' }}>
            {JSON.stringify(optimizationResult, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

export default DeliveryOptimizerSinglePage;
