// src/MapDisplay.js
import React from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';

function MapDisplay({ routeData, defaultCenter }) {
  // If routeData is provided and has coordinates, center on the first coordinate;
  // otherwise use the provided defaultCenter (or fallback to [0,0]).
  const center = routeData && routeData.length > 0 
    ? routeData[0] 
    : defaultCenter || [0, 0];

  return (
    <MapContainer
      center={[51.505, -0.09]}
  zoom={13}
  style={{ height: '400px', width: '100%' }}>

      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution="&copy; OpenStreetMap contributors"
      />

      {routeData && routeData.map((coord, index) => (
        <Marker key={index} position={coord}>
          <Popup>
            Stop {index + 1}: [ {coord[0]}, {coord[1]} ]
          </Popup>
        </Marker>
      ))}
      {routeData && routeData.length > 0 && (
        <Polyline positions={routeData} color="blue" />
      )}
    </MapContainer>
  );
}

export default MapDisplay;
