import React from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';

function MapDisplay({ routeData }) {
  // If routeData exists, center the map on the first coordinate.
  const center = routeData && routeData.length > 0 ? routeData[0] : [0, 0];

  return (
    <MapContainer center={center} zoom={13} style={{ height: "400px", width: "100%" }}>
      <TileLayer
        attribution='&copy; <a href="https://openstreetmap.org">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {routeData.map((coord, index) => (
        <Marker key={index} position={coord}>
          <Popup>
            Stop {index + 1}: [ {coord[0]}, {coord[1]} ]
          </Popup>
        </Marker>
      ))}
      <Polyline positions={routeData} color="blue" />
    </MapContainer>
  );
}

export default MapDisplay;
